INSERT INTO uniqon.middle_classifications (middle_classification_id, feature, image, species, main_classification_id) VALUES (1, '벵골 여우는 주둥이가 길게 늘어져 있고, 귀가 길고, 머리와 몸길이의 약 50~60%에 달하는 부푼 꼬리를 가진 비교적 작은 여우입니다/등부분의 털 색깔은 매우 다양하지만, 대부분 회색빛이고 흐릿한 색깔입니다', 'https://kkini.s3.ap-northeast-2.amazonaws.com/uniqon/AI%EC%82%AC%EC%A7%84/%EB%B2%B5%EA%B3%A8%EC%97%AC%EC%9A%B0AI2.png', '벵골 여우', 1);
INSERT INTO uniqon.middle_classifications (middle_classification_id, feature, image, species, main_classification_id) VALUES (2, '아프간여우는 부드러운 회갈색의 털을 가지고 있으며 배는 하얗습니다/등에는 검은 줄무늬가 있으며 때때로 뒷다리가 검다. 머리는 커다란 귀와 가늘고 짧은 주둥이로 특징지어집니다/꼬리는 몸통과 비슷한 길이이며 북슬북슬하고 그 끝은 검습니다', 'https://kkini.s3.ap-northeast-2.amazonaws.com/uniqon/AI%EC%82%AC%EC%A7%84/%EC%95%84%ED%94%84%EA%B0%84%EC%97%AC%EC%9A%B0AI.png', '아프간 여우', 1);
INSERT INTO uniqon.middle_classifications (middle_classification_id, feature, image, species, main_classification_id) VALUES (3, '케이프여우는 은회색 털을 지녔으며 옆구리와 배는 누런빛입니다/큰 귀와 어두운 입주위가 특징적입니다/꼬리는 북슬북슬하고, 어깨높이는 35cm가량이며 몸무게는 2.5kg~3kg 정도입니다', 'https://kkini.s3.ap-northeast-2.amazonaws.com/uniqon/AI%EC%82%AC%EC%A7%84/%EC%BC%80%EC%9D%B4%ED%94%84%EC%97%AC%EC%9A%B0AI.png', '케이프 여우', 1);
INSERT INTO uniqon.middle_classifications (middle_classification_id, feature, image, species, main_classification_id) VALUES (4, '서남아시아 원산의 사막 도마뱀의 일종으로 레오파드 모프 그대로 표범을 연상케 하는 점무늬가 특징인 도마뱀입니다/발톱과 발가락의 흡반이 없으며 눈꺼풀을 움직일 수 있습니다', 'https://kkini.s3.ap-northeast-2.amazonaws.com/uniqon/AI%EC%82%AC%EC%A7%84/%EB%A0%88%EC%98%A4%ED%8C%8C%EB%93%9CAI.png', '레오파드게코 도마뱀', 2);
INSERT INTO uniqon.middle_classifications (middle_classification_id, feature, image, species, main_classification_id) VALUES (5, '크레스티드 게코의 발색은 회색에서부터 갈색, 붉은색, 주황색, 노란색까지 다채로운 색과 무늬를 가지고 있습니다/그리고 수직 표면도 쉽게 이동할 수 있는 특화된 발바닥을 가지고 있습니다/크레스티드 게코의 꼬리는 물건을 집을 수 있으며 재빠르게 움직일 수 있습니다', 'https://kkini.s3.ap-northeast-2.amazonaws.com/uniqon/AI%EC%82%AC%EC%A7%84/%ED%81%AC%EB%A0%88%EC%8A%A4%ED%8B%B0%EB%93%9CAI.png', '크레스티드 게코 도마뱀', 2);
INSERT INTO uniqon.middle_classifications (middle_classification_id, feature, image, species, main_classification_id) VALUES (6, '턱수염도마뱀은 사납게 생긴 외모와는 달리 매우 온순합니다/성체가 되면 하루의 대부분을 그냥 한가롭게 햇빛이나 쬐고 있을 정도로 느긋한 성격이고, 새끼 시절부터 핸들링을 자주 해 줬다면 사람을 물 일도 거의 없습니다', 'https://kkini.s3.ap-northeast-2.amazonaws.com/uniqon/AI%EC%82%AC%EC%A7%84/%ED%84%B1%EC%88%98%EC%97%BC%EB%8F%84%EB%A7%88%EB%B1%80AI2.png', '턱수염 도마뱀', 2);
INSERT INTO uniqon.middle_classifications (middle_classification_id, feature, image, species, main_classification_id) VALUES (7, '앞발 사이에 복갑의 막대기와 같은 부분이 쟁기와 닮았다고 하여서 붙여진 이름입니다/등딱지는 높은 돔 각 직사각형의 방패에 눈에 잘 띄는 성장고리와 갈색빛입니다/등껍질의 크기는 43cm정도이며 수컷의 경우 전체 길이는 41cm에 10kg정도, 암컷의 경우 37cm에 8.8kg 정도의 무게를 가집니다', 'https://kkini.s3.ap-northeast-2.amazonaws.com/uniqon/AI%EC%82%AC%EC%A7%84/%EC%9F%81%EA%B8%B0%EA%B1%B0%EB%B6%81AI.png', '쟁기 거북', 3);
INSERT INTO uniqon.middle_classifications (middle_classification_id, feature, image, species, main_classification_id) VALUES (8, '붉은귀거북의 등딱지(갑)는 부드러우며 완만하게 구부러져 있습니다/등딱지는 진초록색에 노란색의 줄무늬가 있습니다/눈의 바로 뒤쪽에 붉은색 무늬가 있는 것이 특징이며, 여기에서 붉은귀거북이라는 이름이 붙었습니다', 'https://kkini.s3.ap-northeast-2.amazonaws.com/uniqon/AI%EC%82%AC%EC%A7%84/%EB%B6%89%EC%9D%80%EA%B7%80%EA%B1%B0%EB%B6%81AI.png', '붉은귀 거북', 3);
INSERT INTO uniqon.middle_classifications (middle_classification_id, feature, image, species, main_classification_id) VALUES (9, '커먼 머스크 터틀의 특징은 어두운 몸색깔과 얼굴에 있는 흰줄기, 그리고 무엇보다 앙증맞은 작은 체구입니다/몸 길이는 8~10cm전 후, 최대신장 13.6cm정도입니다', 'https://kkini.s3.ap-northeast-2.amazonaws.com/uniqon/AI%EC%82%AC%EC%A7%84/%EC%BB%A4%EB%A8%BC%EB%A8%B8%EC%8A%A4%ED%81%AC%ED%84%B0%ED%8B%80AI.png', '커먼 머스크 터틀', 3);
INSERT INTO uniqon.middle_classifications (middle_classification_id, feature, image, species, main_classification_id) VALUES (10, '몸 색깔은 녹색이며 이마, 얼굴과 꼬리깃 일부가 빨간색이 특징입니다/날개 끝과 꼬리 일부는 청색입니다/보통 2~3마리 정도로 생활하며 나무 위에서 먹이를 찾고 나는 속도가 빠르며 높이 납니다', 'https://kkini.s3.ap-northeast-2.amazonaws.com/uniqon/AI%EC%82%AC%EC%A7%84/%EA%B8%88%EA%B0%95%EC%95%B5%EB%AC%B4AI.png', '금강 앵무', 4);
INSERT INTO uniqon.middle_classifications (middle_classification_id, feature, image, species, main_classification_id) VALUES (11, '몸길이는 약 15cm 내외, 몸무게는 40~60g 정도입니다/아프리카 원산의 앵무로 파트너에 대한 애정이 깊기 때문에 \'러브버드\'라고도 불려요. 연한 핑크색을 띠고 있는 모습에서 이름이 지어졌습니다', 'https://kkini.s3.ap-northeast-2.amazonaws.com/uniqon/AI%EC%82%AC%EC%A7%84/%EB%AA%A8%EB%9E%80%EC%95%B5%EB%AC%B4AI.png', '모란 앵무', 4);
INSERT INTO uniqon.middle_classifications (middle_classification_id, feature, image, species, main_classification_id) VALUES (12, '초록색과 파랑이 혼합된 화려한 앵무입니다/꽃의 꿀을 먹기에 적합하도록 브러시 형태의 혀를 가지고 있다. 과즙을 주로 먹기 때문에 물이 많이 섞인 변을 보는 것이 특징입니다/저지의 삼림에 살며 꽃 꿀, 열매 등을 먹습니다', 'https://kkini.s3.ap-northeast-2.amazonaws.com/uniqon/AI%EC%82%AC%EC%A7%84/%EC%98%A4%EC%83%89%EC%95%B5%EB%AC%B4AI.png', '오색 앵무', 4);
INSERT INTO uniqon.middle_classifications (middle_classification_id, feature, image, species, main_classification_id) VALUES (13, '구피는 성적 이형성을 가지고 있습니다/야생의 구피 암컷은 무늬가 없고 몸 전체가 회색을 띄지만, 수컷은 몸과 꼬리에 다양한 색상의 화려한 무늬나 점 등으로 암컷을 유혹합니다/이로 인해 포식자에게 더 발견되기 쉬워질 수 있습니다.', 'https://kkini.s3.ap-northeast-2.amazonaws.com/uniqon/AI%EC%82%AC%EC%A7%84/%EA%B5%AC%ED%94%BCAI.png', '구피', 5);
INSERT INTO uniqon.middle_classifications (middle_classification_id, feature, image, species, main_classification_id) VALUES (14, '화려한 무늬와 머리위에 돋아난 큰 혹이 특징으로 성체는 25~30cm까지 자라는 대형 시클리드입니다/수명은 약 8~10년 정도 되며, 우리나라에선 사람을 알아보고 잘 따르는 것처럼 보여서 \'워터독, 물강아지\'라고도 부릅니다', 'https://kkini.s3.ap-northeast-2.amazonaws.com/uniqon/AI%EC%82%AC%EC%A7%84/%ED%94%8C%EB%9D%BC%EC%9B%8C%ED%98%BCAI.png', '플라워혼', 5);
INSERT INTO uniqon.middle_classifications (middle_classification_id, feature, image, species, main_classification_id) VALUES (15, '플래티는 어항을 부지런하게 누비고 다니는 작고 귀여운 모습의 열대어입니다/동그랗고 놀란 듯한 눈이 매력적인 플래티는 온순한 성격으로 비슷한 크기의 다른 열대어들과 같이 키워도 싸우지 않고 잘 어울리지만, 플래티 수컷끼리는 공격적인 모습을 보이기도 합니다', 'https://kkini.s3.ap-northeast-2.amazonaws.com/uniqon/AI%EC%82%AC%EC%A7%84/%ED%94%8C%EB%9E%98%ED%8B%B0AI.png', '플래티', 5);
INSERT INTO uniqon.middle_classifications (middle_classification_id, feature, image, species, main_classification_id) VALUES (16, '킹코브라의 피부는 짙은올리브 또는 갈색이며 검은색, 흰색 또는 노란색 밴드가 교차되어 있습니다/주둥이 가까운 곳, 눈쪽에 두 줄의 가로 무늬가 있습니다/몸길이와 체중은 지역 및 기타 요인에 따라서 변화가 심합니다', 'https://kkini.s3.ap-northeast-2.amazonaws.com/uniqon/AI%EC%82%AC%EC%A7%84/%ED%82%B9%EC%BD%94%EB%B8%8C%EB%9D%BCAI.png', '킹코브라', 6);
INSERT INTO uniqon.middle_classifications (middle_classification_id, feature, image, species, main_classification_id) VALUES (17, '전체적인 외형은 다 알록달록 화려한 색이 얼핏 보면 산호 뱀과 상당히 유사하지만 전혀 다른 뱀으로 밀크 스네이크는 독이 없습니다/독이 있는 산호 뱀과 구별하는 방법은 붉은 줄무늬가 노란색과 붙어있는지, 검은색과 붙어있는지를 확인하면 구별하기 쉽습니다', 'https://kkini.s3.ap-northeast-2.amazonaws.com/uniqon/AI%EC%82%AC%EC%A7%84/%EB%B0%80%ED%81%AC%EC%8A%A4%EB%84%A4%EC%9D%B4%ED%81%ACAI.png', '밀크스네이크', 6);
INSERT INTO uniqon.middle_classifications (middle_classification_id, feature, image, species, main_classification_id) VALUES (18, '볼파이톤은 작은 체구와 귀여운 외모, 온순한 성격, 튼튼한 체질, 저렴한 가격으로 인해 애완 뱀에 처음 입문하는 사람들에게 이상적인 초보 입문용 뱀으로 킹스네이크, 콘스네이크와 함깨 입문용 스네이크로 평가됩니다/뱀의 암수 구별은 불가능하지는 않지만, 힘듭니다', 'https://kkini.s3.ap-northeast-2.amazonaws.com/uniqon/AI%EC%82%AC%EC%A7%84/%EB%B3%BC%ED%8C%8C%EC%9D%B4%ED%86%A4AI.png', '볼파이톤', 6);
INSERT INTO uniqon.middle_classifications (middle_classification_id, feature, image, species, main_classification_id) VALUES (19, '베일드 카멜레온은 카멜레온과의 카멜레온속에 속하는 파충류입니다/다른 카멜레온들보다 머리 위에 훨씬 높게 솟은 투구와 마치 눌린 듯 초록색 나뭇잎처럼 생긴 납작한 몸이 특징입니다', 'https://kkini.s3.ap-northeast-2.amazonaws.com/uniqon/AI%EC%82%AC%EC%A7%84/%EB%B2%A0%EC%9D%BC%EB%A6%ACAI.png', '베일드 카멜레온', 7);
INSERT INTO uniqon.middle_classifications (middle_classification_id, feature, image, species, main_classification_id) VALUES (20, '체형이 세로로 넓직하며, 돌출된 두 안구는 360º로 따로 돌아가고, 작은 구멍이 뚫린 눈꺼풀이 항시 덮고 있습니다/발도 두갈래로 나뉘어서 나뭇가지 등을 \'잡는 데\' 유리한 모양입니다/눈을 각각 360도로 굴리는 능력이 있어서 시야에 있어 사실상 사각이 거의 없습니다', 'https://kkini.s3.ap-northeast-2.amazonaws.com/uniqon/AI%EC%82%AC%EC%A7%84/%ED%94%BC%EA%B7%B8%EB%AF%B8AI.png', '피그미 카멜레온', 7);
INSERT INTO uniqon.middle_classifications (middle_classification_id, feature, image, species, main_classification_id) VALUES (21, '세계에서 가장 큰 카멜레온 (보통 몸무게로는 가장 크지만, 길이로는 가장 큰 타이틀을 가진 왕카멜레온보다 작습니다) 중에서 수컷 파슨카멜레온은 눈 위부터 코까지 이어지는 융기선을 가지고 있으며, 두 개의 사마귀 뿔을 형성하고 있습니다/널리 알려진 2종의 아종이 있습니다', 'https://kkini.s3.ap-northeast-2.amazonaws.com/uniqon/AI%EC%82%AC%EC%A7%84/%ED%8C%8C%EC%8A%A8AI.png', '파슨 카멜레온', 7);
INSERT INTO uniqon.middle_classifications (middle_classification_id, feature, image, species, main_classification_id) VALUES (22, '아펜핀셔의 키는 25-30cm, 몸무게 3-47kg로 소형견이며 토이그룹에 속합니다/작지만 골격이 튼튼하고 동그랗게 반짝이는 작은 눈 때문에 원숭이처럼 보입니다/몸 전체가 덥수룩한 털로 뒤덮여 있고, 입 주위에 턱수염 같은 털이 수북이 나있어 우스꽝스럽게 보입니다', 'https://kkini.s3.ap-northeast-2.amazonaws.com/uniqon/AI%EC%82%AC%EC%A7%84/%EC%95%84%ED%8E%9C%ED%95%80%EC%85%94AI.png', '아펜 핀셔', 8);
